interface ContainerProps {
    pageName: string;
}


const reducer = (state: any, action: any) => {
    switch (action.type) {
        case 'default':
            return {
                ...state,
                [action.type]: [action.payload]
            }
        default:
            return {
                ...state,
                [action.type]: [action.payload]
            }
    }
}

// Get a reference to the database service
